function SubmitChangePassword(){var n=$("#frmChangePassword").serialize();$.ajax({url:"/Profile/SubmitChangePassword",type:"POST",cache:!1,data:n,beforeSend:function(){$(".preloader").show()},success:function(n){HidePreloader();n!==""&&n.code===200?ShowPopupNotify(n.message):($(".error-general").text(n.errormessage),$(".error-general").show())},error:function(){HidePreloader();alert(defaultErrorMessage);location.reload()}})}$(document).ready(function(){$.validator.addMethod("notEqual",function(n,t,i){return this.optional(t)||n!=$(i).val()},"Mật khẩu mới không được trùng mật khẩu cũ");$(".popup-send .close-send").click(function(){$(".popup-send .txt").text()=="Cập nhật mật khẩu thành công"&&(window.location.href="/logout?requirelogin=true")});$("#frmChangePassword").validate({errorElement:"div",errorClass:"error",rules:{OldPassword:{required:!0,minlength:6,maxlength:20},NewPassword:{required:!0,notEqual:"#pass-current",minlength:6,maxlength:20},ConfirmNewPassword:{required:!0,equalTo:"#pass-new",minlength:6,maxlength:20}},messages:{OldPassword:{required:"Vui lòng nhập mật khẩu hiện tại",minlength:"Mật khẩu hiện tại quá ngắn, vui lòng nhập tối thiểu 6 ký tự",maxlength:"Mật khẩu hiện tại quá dài, vui lòng nhập tối đa 20 ký tự"},NewPassword:{required:"Vui lòng nhập mật khẩu mới",minlength:"Mật khẩu mới quá ngắn, vui lòng nhập tối thiểu 6 ký tự",maxlength:"Mật khẩu mới quá dài, vui lòng nhập tối đa 20 ký tự"},ConfirmNewPassword:{required:"Vui lòng nhập lại mật khẩu mới",minlength:"Mật khẩu mới quá ngắn, vui lòng nhập tối thiểu 6 ký tự",maxlength:"Mật khẩu mới quá dài, vui lòng nhập tối đa 20 ký tự",equalTo:"Vui lòng nhập mật khẩu trùng khớp nhau"}}});$("#frmChangePassword").submit(function(n){console.log("ab");$("#frmChangePassword").valid()&&(n.preventDefault(),SubmitChangePassword())})});