$(document).ready(function(){$(".btn-readmore").click(function(){let n=$(this),t=n.parent().find(".listblog"),i=parseInt(n.attr("data-index"))+1;$.ajax({url:"/News/GetNews",data:{pageIndex:i,pageSize:parseInt(n.attr("data-size"))}}).done(function(r){t.append(r);let u=parseInt(n.attr("data-total"))-t.children().length-3;u>0?(n.attr("data-index",i),n.find("span").text("Xem thêm "+u+" tin tức")):n.remove()})})});